import React, { useState,useEffect} from 'react';
import { useDispatch,useSelector} from 'react-redux'
import  '../SearchBar/SeachBar.css'
import {GetName} from '../../actions/index'

export const SearchBar = () => {
	

	const[name,setName] = useState("")
	const dispatch = useDispatch()
	const detail = useSelector(state => state.pokemonDetails)
	const handleChange = (e) => {
		const nombre = e.target.value
		console.log(nombre)
		setName(nombre)
	}
	const handleSubmit = (name)=>{
		dispatch(GetName(name))
		setName(detail)
		console.log(detail)
	}
	useEffect(() => {
		handleSubmit(name)
	},[])
	return (
		<div className="search-bar">
			<div className='div_menu'>
				<div className='div_title'>
					<h2 className='title'>Search a Pokemon</h2>
				</div>
				<form >
					<input
						className='input'
						type='text'
						autoComplete='off'
						onChange={handleChange}	
					/>
					<button className='btn_seatch' type='submit' onClick={handleSubmit}>
							Buscar
					</button>
				</form>
			</div>
		</div>
	);
};

export default SearchBar;
