import '../Home/Home.css';
import React , { useEffect, useState} from 'react'

import {connect} from 'react-redux'
import {getPokemons} from '../../actions/index'
import Navbar from '../Nav/NavBar'
import axios from 'axios'
import {Link} from 'react-router-dom';

function Home({getPokemons,state}) {
  const [pokemons, setPokemons] = useState([]);
  useEffect(()=>{
    getPokemons()
  },[])
  useEffect(() => {
    setPokemons(state)
    console.log(pokemons)
  },[state])

  return (
    <div className="container-pokes">
      {
        pokemons && pokemons.map(p => {
          return (
            
            <div className = "poke" key={p.id}>
            <Link to={`/pokemons/${p.id}`}>
              <h4>{p.name}</h4>
            </Link>
              <img src={p.sprite} alt="" />
              {
                p && p.Types.map(type => {
                  return(
                    <div className="tipos" key={type.name}>
                    <p>{type.name}</p>
                    </div>
                  )
                })
              }
            </div>
            
          )
        })
      }
      
    </div>
  );
}
const feliToProps = (state) => {
  return {
    state: state.pokemonHome
  }
}
const mapDispatchToProps = (dispatch) => {
  return{
    getPokemons: () => dispatch(getPokemons())
  }
}

export default connect(feliToProps,mapDispatchToProps)(Home);

