import { ADDPOKEMON } from "../actions/index";
import { GETCREATE } from "../actions/index";
import { GETDETAILS } from "../actions/index";
import { GETNAME } from "../actions/index";
import { GETPOKEMONS } from "../actions/index";
import { GETTYPES } from "../actions/index";

const initialState = {
    pokemonDetails: [],
    pokemonName: [],
    loading: false,
    pokemonHome: [],
    types: [],
    pokemonCreate: [],
    pokemonFilter: [],
    CreateFilter: [],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GETPOKEMONS:
            return {
                ...state,
                pokemonHome: action.payload,
            };

        case GETDETAILS:
            return {
                ...state,
                pokemonDetails: action.payload,
                loading: false,
            };

        case GETTYPES:
            return {
                ...state,
                types: action.payload,
            };

        case GETNAME:
            return {
                ...state,
                pokemonName: action.payload,
                loading: true,
            };
        case GETCREATE:
            return {
                ...state,
                pokemonCreate: action.payload,
            };
        case ADDPOKEMON:
            return {
                ...state,
            };

        default:
            return {...state };
    }
};

export default rootReducer;
