import React,{useEffect,useState} from 'react'
import { connect } from "react-redux";
import '../PokemonDetail/PokemonDetail.css'
import { getDetails,GetName } from "../../actions/index";
const PokemonDetail = (props) => {
    const[poke,setPoke] = useState([])
    let id = props.match.params.id
    useEffect(() => {
        props.getDetails(id);
        console.log(id)
      }, [id]);
      
    return (
        <div>
            {
        <div className="container">
          
            
          <div className="subcontainerstats">
            <ul>
              <li>HP: {props.state.health}</li>
              <li>weight: {props.state.weight}</li>
              <li>height: {props.state.height}</li>
              <li>attack: {props.state.attack}</li>
              <li>defense: {props.state.defense}</li>
              <li>speed: {props.state.speed}</li>
              <div>
                {props.state.tipos
                  ? props.state.tipos.map((t) => 
                  <img key={t}
                  className="typeImg"
                  src={`/images/Tipo_Pokemons/${t}.gif`}
                  alt="no encontrada"
                />)
                  : ""}
              </div>
            </ul>
          </div>
        </div>
      }
        </div>
    )
}
const mapStateToProps = (state) => {
    return {
      state: state.pokemonDetails,
    };
  };
  
  const mapDispatchToProps = (dispatch) => {
    return {
      getDetails: (id) => dispatch(getDetails(id)),
      GetName : (id) => dispatch(GetName(id))
    };
  };
export default connect(mapStateToProps,mapDispatchToProps)(PokemonDetail)
