import React, { Fragment , useEffect, useState} from "react";
import { connect } from "react-redux";
import { addPokemons,GetTypes } from "../../actions/index";
import '../Creation/Creation.css'
import axios from 'axios'
import {FORMS,POKEMON_URL} from '../../utils/constants'
const Validate = (data) => {
	let errors = {};
	let reg = /^\d+$/;
	if (!data.name) {
		errors.name = 'Name is required';
	}
	if (!data.hp || data.hp.search(reg) === -1) {
		errors.hp = 'HP is required and it must be a number';
	}
	if (!data.attack || data.attack.search(reg) === -1) {
		errors.attack = 'Attack is required and it must be a number';
	}
	if (!data.defense || data.defense.search(reg) === -1) {
		errors.defense = 'Defense is required and it must be a number';
	}
	if (!data.speed || data.speed.search(reg) === -1) {
		errors.speed = 'Speed is required and it must be a number';
	}
	if (!data.height || data.height.search(reg) === -1) {
		errors.height = 'Height is required and it must be a number';
	}
	if (!data.weight || data.weight.search(reg) === -1) {
		errors.weight = 'Weight is required and it must be a number';
	}
	return errors;
};

export function Creation({pokemonTypes, GetTypes,addPokemons}) {
	const [Data, setData] = useState({
			name: '',
			hp: '',
			attack: '',
			defense: '',
			speed: '',
			height: '',
			weight: '',
			type: [],
			sprite: "",
	});
	const [Types, setTypes] = useState([]);
	const [Errors, setErrors] = useState({});
	const [Alert, setAlert] = useState({ errors: false, create: false });

	useEffect(() => {
			GetTypes();
	}, [GetTypes]);
	useEffect(() => {
			setErrors(Validate(Data));
			console.log("errors",Errors)
	}, [Data]);
	useEffect(() => {
			if (!Object.keys(Errors).length) {
			setAlert({...Alert, errors: false });
			console.log("alert",Alert)
		}
	}, [Errors]);
  
	useEffect(() => {
		if (Types.length) {
		let total = []
		pokemonTypes.map((t,i) => {
			if(Types.includes(t) === true){
			total.push(i+1)
			}
		})
				setData({...Data, type: Types});
		}
	}, [Types]);
	const handleSubmit = async (e) => {
		
		console.log("Esto es types: " + Types)
			e.preventDefault();
			try {
				await axios.post('http://localhost:3002/pokemons', Data);
			} catch (err) {
				console.log(err);
			}
	};
    const handleChange = (e) => {
		setData({...Data, [e.target.name]: e.target.value});
	};
    const handleTypes = (e) => {
		if (Types.length < 2) {
			if (!Types.includes(e.target.value)) {
				setTypes([...Types, e.target.value]);
			}
		} else setTypes([e.target.value]);
	};
  return(
    <form onSubmit={(e) => handleSubmit(e)}>
				<div className='div_g'>
					<div>
						<h1 className='title-create'>Create a Pokemon</h1>
					</div>
					{FORMS &&
						FORMS.map((el, i) => (
							<div key={i} className='div_f'>
								<div key={i} className='div_label'>
									<label key={i} className='label'>
										{el.label}:
									</label>
								</div>
								<input
									className='input'
									key={el.name}
									name={el.name}
									type='text'
									autoComplete='off'
									onChange={(e) => handleChange(e)}
								/>
							</div>
						))}
            <div className='div_types'>
						<select onChange={(e) => handleTypes(e)}>
							{pokemonTypes &&
								pokemonTypes.map((t, i) => (
									<option key={i} value={i+1}>
										{t}
									</option>
								))}
						</select>
						<div className='div_type_container'>
            ðŸ§¬Tipos
							{Types &&
								Types.map((el, i) => (
									<div key={i} className='div_type'>
										<label className='label'>{el}</label>
									</div>
								))}
						</div>
					</div>
					<div className='div_submit'>
						<button className='btn_submit' type='submit' >
							Create a Pokemon
						</button>
					</div>
				</div>
			</form>
  )

};
const mapStateToProps = (state) => {
  return {
    pokemonTypes: state.types,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addPokemons: (pokemon) => dispatch(addPokemons(pokemon)),
    GetTypes: () => dispatch(GetTypes())
}}
export default connect(mapStateToProps, mapDispatchToProps)(Creation);
