import { ADDPOKEMON } from "../actions/index";
import { GETCREATE } from "../actions/index";
import { GETDETAILS } from "../actions/index";
import { GETNAME } from "../actions/index";
import { GETPOKEMONS } from "../actions/index";
import { GETTYPES } from "../actions/index";
import { POKEMONS_ORDER, POKEMONS_ORIGIN, POKEMON_TYPES } from '../actions/index'
export const GetPokemonOrder = (order, array) => {
    switch (order) {
        case 'A-Z':
            return array.sort((a, b) => {
                if (a.name > b.name) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'Z-A':
            return array.sort((a, b) => {
                if (a.name < b.name) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'More Attack':
            return array.sort((a, b) => {
                if (a.attack < b.attack) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'Less Attack':
            return array.sort((a, b) => {
                if (a.attack > b.attack) {
                    return 1;
                } else {
                    return -1;
                }
            });

        default:
            return array;
    }
};

const initialState = {
    pokemonDetails: [],
    pokemonName: [],
    loading: false,
    pokemonHome: [],
    pokemonViews: {
        all: [],
        now: [],
    },
    types: [],
    pokemonCreate: [],
    pokemonFilter: [],
    CreateFilter: [],
    pokemonsTypes: [],
    pokemonFilter: 'All',
    pokemonOrigin: 'All',
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GETPOKEMONS:
            return {
                ...state,
                pokemonHome: action.payload,
            };

        case GETDETAILS:
            return {
                ...state,
                pokemonDetails: action.payload,
                loading: false,
            };

        case GETTYPES:
            return {
                ...state,
                types: action.payload,
            };

        case GETNAME:
            return {
                ...state,
                pokemonHome: action.payload,
                loading: true,
            };
        case GETCREATE:
            return {
                ...state,
                pokemonCreate: action.payload,
            };
        case ADDPOKEMON:
            return {
                ...state,
            };
        case POKEMONS_ORDER:
            return {
                ...state,
                pokemonViews: {
                    ...state.pokemonViews,
                    now: GetPokemonsViews(
                        GetPokemonOrder(action.payload, state.pokemonViews.all)
                    ),
                },
                pokemonFilter: action.payload,
            };
        case POKEMONS_ORIGIN:
            return {
                ...state,
                pokemonViews: {
                    ...state.pokemonViews,
                    now: GetPokemonsViews(GetPokemonOrigin(action.payload, state.pokemons)),
                    all: GetPokemonOrigin(action.payload, state.pokemons),
                },

                pokemonOrigin: action.payload, /// API
                pokemonFilter: action.payload,
            };
        case POKEMON_TYPES:
            state.pokemonViews.all = GetPokemonType(
                action.payload,
                GetPokemonOrigin(state.pokemonOrigin, state.pokemons)
            );
            return {
                ...state,
                pokemonViews: {
                    ...state.pokemonViews,
                    now: GetPokemonsViews(state.pokemonViews.all),
                },
                pokemonFilter: action.payload,
            };

        default:
            return {...state };
    }
};

export default rootReducer;
