import React,{useEffect,useState} from 'react'
import { connect } from "react-redux";
import '../PokemonDetail/PokemonDetail.css'
import { getDetails,GetName } from "../../actions/index";
const PokemonDetail = (props) => {
    const[poke,setPoke] = useState([])
    let id = props.match.params.id
    useEffect(() => {
        props.getDetails(id);
        console.log(id)
      }, [id]);
      
    return (
        <div className="poke-detail">
             <img src={props.state[0].sprite} alt="" />
             <h1>Name: {props.state[0] && props.state[0].name}</h1>
             <h3>HP: {props.state[0].hp}</h3>
             <h3>Attack: {props.state[0].attack}</h3>
             <h3>Defense: {props.state[0].defense}</h3>
             <h3>Speed: {props.state[0].speed}</h3>
             <h3>Height: {props.state[0].height}</h3>
             <h3>Weight: {props.state[0].weight}</h3>
             
        </div>
    )
}
const mapStateToProps = (state) => {
    return {
      state: state.pokemonDetails,
    };
  };
  
  const mapDispatchToProps = (dispatch) => {
    return {
      getDetails: (id) => dispatch(getDetails(id)),
      GetName : (id) => dispatch(GetName(id))
    };
  };
export default connect(mapStateToProps,mapDispatchToProps)(PokemonDetail)
