import React, { Fragment , useEffect, useState} from "react";
import { connect } from "react-redux";
import { addPokemons,GetTypes } from "../../actions/index";
import '../Creation/Creation.css'
import axios from 'axios'
import {FORMS, SPRITES,SPRITE_DEFAULT,POKEMON_URL} from '../../utils/constants'

export function Creation({pokemonTypes, GetTypes,addPokemons}) {
  const [Data, setData] = useState({
		name: '',
		hp: '',
		attack: '',
		defense: '',
		speed: '',
		height: '',
		weight: '',
		type: [],
		sprite: "",
	});
  const [Types, setTypes] = useState([]);
  useEffect(() => {
		GetTypes();
	}, [GetTypes]);
  
  useEffect(() => {
	if (Types.length) {
      let total = []
      pokemonTypes.map((t,i) => {
        if(Types.includes(t) === true){
          total.push(i+1)
        }
      })
			setData({...Data, type: Types});
	}
}, [Types]);
  const handleSubmit = async (e) => {
    console.log("Esto es types: " + Types)
		e.preventDefault();
		
			try {
				await axios.post(POKEMON_URL, Data);
			} catch (err) {
				console.log(err);
			}
		
	};
  const handleChange = (e) => {
		setData({...Data, [e.target.name]: e.target.value});
	};
  const handleTypes = (e) => {
		if (Types.length < 2) {
			if (!Types.includes(e.target.value)) {
				setTypes([...Types, e.target.value]);
			}
		} else setTypes([e.target.value]);
	};
  return(
    <form onSubmit={(e) => handleSubmit(e)}>
				<div className='div_g'>
					<div>
						<h1 className='title-create'>Create a Pokemon</h1>
					</div>
					{FORMS &&
						FORMS.map((el, i) => (
							<div key={i} className='div_f'>
								<div key={i} className='div_label'>
									<label key={i} className='label'>
										{el.label}:
									</label>
								</div>
								<input
									className='input'
									key={el.name}
									name={el.name}
									type='text'
									autoComplete='off'
									onChange={(e) => handleChange(e)}
								/>
							</div>
						))}
            <div className='div_types'>
						<select onChange={(e) => handleTypes(e)}>
							{pokemonTypes &&
								pokemonTypes.map((t, i) => (
									<option key={i} value={i+1}>
										{t}
									</option>
								))}
						</select>
						<div className='div_type_container'>
            🧬Tipos
							{Types &&
								Types.map((el, i) => (
									<div key={i} className='div_type'>
										<label className='label'>{el}</label>
									</div>
								))}
						</div>
					</div>
					<div className='div_submit'>
						<button className='btn_submit' type='submit'>
							Create a Pokemon
						</button>
					</div>
				</div>
			</form>
  )








