import React, { useState,useEffect} from 'react';
import { useDispatch,useSelector} from 'react-redux'
import  '../SearchBar/SeachBar.css'
import {GetName} from '../../actions/index'
import Pokemon from '../Pokemon/Pokemon'

export const SearchBar = (props) => {
	

	const[name,setName] = useState("")
	const[poke,setPoke] = useState([])
	const dispatch = useDispatch()
	const detail = useSelector(state => state.pokemonDetails)
	useEffect(() => {
		console.log("detail" + detail)
	},[])
	const handleChange = (e) => {
		setName(e.target.value)
		console.log(name)
	}
	const handleSubmit = (e)=>{
		e.preventDefault();
		if(name.length){
		dispatch(GetName(name))
		}
		setPoke(detail)
		setName("")
		
		console.log(poke)	
	}
	return (
		<>
		<div className="search-bar">
			<div className='div_menu'>
				<div className='div_title'>
					<h2 className='title'>Search a Pokemon</h2>
				</div>
				<form >
					<input
						className='input-search'
						type='search'
						
						autoComplete='off'
						onChange={handleChange}	
					/>
					<button className='btn_seatch' type='submit' onClick={handleSubmit}>
							Buscar
					</button>
				</form>
			</div>
			
		</div>
		</>
	);
};

export default SearchBar;
