import { ADDPOKEMON } from "../actions/index";
import { GETCREATE } from "../actions/index";
import { GETDETAILS } from "../actions/index";
import { GETNAME } from "../actions/index";
import { GETPOKEMONS } from "../actions/index";
import { GETTYPES } from "../actions/index";
import { POKEMONS_ORDER, POKEMONS_ORIGIN, POKEMON_TYPES } from '../actions/index'
export const GetPokemonOrder = (order, array) => {
    let arr = []
    arr.push(array)
    switch (order) {
        case 'A-Z':
            return arr.sort((a, b) => {
                if (a.name > b.name) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'Z-A':
            return arr.sort((a, b) => {
                if (a.name < b.name) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'More Attack':
            return arr.sort((a, b) => {
                if (a.attack < b.attack) {
                    return 1;
                } else {
                    return -1;
                }
            });
        case 'Less Attack':
            return arr.sort((a, b) => {
                if (a.attack > b.attack) {
                    return 1;
                } else {
                    return -1;
                }
            });

        default:
            return arr;
    }
};
export const GetPokemonOrigin = (origin, array) => {
    switch (origin) {
        case 'API':
            return array.filter((el) => typeof el.id === 'number');

        case 'Companion App':
            let reg_ex = /-/;
            return array.filter((el) => el.id.toString().search(reg_ex) !== -1);

        case 'All':
            return array;

        default:
            return array;
    }
};
export const GetPokemonType = (type, array) => {
    if (type === 'All') return array;
    let newArray = array.filter((el, i) =>
        el.Type.length ?
        el.Type[0].name === type ?
        true :
        el.Type.length > 1 ?
        el.Type[1] === type ?
        true :
        false :
        false :
        false
    );
    return newArray;
};

export const GetPokemonsViews = (array, min, max) => {
    if (!min && !max) {
        return array.slice(0, 12);
    }
    return array.slice(min, max);
};

const initialState = {
    pokemonDetails: [],
    pokemonName: [],
    loading: false,
    pokemonHome: [],
    pokemonViews: {
        all: [],
        now: [],
    },
    types: [],
    pokemonCreate: [],
    pokemonFilter: [],
    CreateFilter: [],
    pokemonsTypes: [],
    pokemonFilter: 'All',
    pokemonOrigin: 'All',
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GETPOKEMONS:
            return {
                ...state,
                pokemonHome: action.payload,
            };

        case GETDETAILS:
            return {
                ...state,
                pokemonDetails: action.payload,
                loading: false,
            };

        case GETTYPES:
            return {
                ...state,
                types: action.payload,
            };

        case GETNAME:
            return {
                ...state,
                pokemonHome: action.payload,
                loading: true,
            };
        case GETCREATE:
            return {
                ...state,
                pokemonHome: action.payload,
            };
        case ADDPOKEMON:
            return {
                ...state,
            };
        case POKEMONS_ORDER:
            return {
                // ...state,
                // pokemonViews: {
                //     ...state.pokemonViews,
                //     now: GetPokemonsViews(
                //         GetPokemonOrder(action.payload, state.pokemonViews.all)
                //     ),
                // },
                // pokemonFilter: action.payload,
                ...state,
                pokemonHome: GetPokemonOrder(action.payload)
            };
        case POKEMONS_ORIGIN:
            return {
                ...state,
                pokemonViews: {
                    ...state.pokemonViews,
                    now: GetPokemonsViews(GetPokemonOrigin(action.payload, state.pokemons)),
                    all: GetPokemonOrigin(action.payload, state.pokemons),
                },

                pokemonOrigin: action.payload, /// API
                pokemonFilter: action.payload,
            };
        case POKEMON_TYPES:
            state.pokemonViews.all = GetPokemonType(
                action.payload,
                GetPokemonOrigin(state.pokemonOrigin, state.pokemons)
            );
            return {
                ...state,
                pokemonViews: {
                    ...state.pokemonViews,
                    now: GetPokemonsViews(state.pokemonViews.all),
                },
                pokemonFilter: action.payload,
            };

        default:
            return {...state };
    }
};

export default rootReducer;
