import React,{useEffect,useState} from 'react'
import '../Filtros/Filtros.css'
import {connect} from 'react-redux'
import {GetTypes,setPokemonsOrder,setPokemonsType,GetCreate, getPokemons, GetApi} from '../../actions/index'

const Filtros = ({GetApi, pokemonTypes, setPokemonsOrder,setPokemonsType,GetCreate,getPokemons,GetTypes}) => {
    const[poke,setPoke] = useState([])
	const[type,setType] = useState("")
		 const GetPokemonType = (tipo, array) => {
		if (type === 'All') return array;
		let newArray = poke.filter((el, i) =>
			el.Types.length ?
			el.Types[0].name === type ?
			true :
			el.Types.length > 1 ?
			el.Types[1].name === type ?
			true :
			false :
			false :
			false
		);
		setPoke(newArray);
	};
	useEffect(() => {
        GetTypes()
        
    },[])
    const handleFilter = (e) => {
		setPokemonsOrder(e.target.value);
		setType(e.target.value)
        
	};
    const handleTypes = (e) => {
            setPokemonsType(e.target.value);
            
    };
	const handleOrigin = (e) => {
		if(e.target.value === "Creados"){
        GetCreate()
        }
        if(e.target.value === "All"){
            getPokemons()
        }
        if(e.target.value === "API"){
            GetApi()
        }
	};
	
    
    return (
        <div>
            <select name='Filter' onChange={(e) => handleFilter(e)}>
				<option value='A-Z'>A-Z</option>
				<option value='Z-A'>Z-A</option>
				<option value='MÃ¡s Ataque'>ðŸ’ªMÃ¡s Ataque</option>
				<option value='Menos Ataque'>ðŸ˜ŒMenos Ataque</option>
			</select>
			<select name='Type' onChange={(e) => handleTypes(e)}>
				<option value='All'>Todos</option>
				{pokemonTypes &&
					pokemonTypes.map((t, i) => (
						<option key={i} value={t}>
                        {t === "fire" ? "ðŸ”¥" : t}
                       
						</option>
					))}
			</select>
			<select name='Origin' onChange={(e) => handleOrigin(e)}>
				<option value='Todos'>Todos</option>
				<option value='API'>API</option>
				<option value='Creados'>Creados</option>
			</select>
        </div>
    )
}

const mapStateToProps = (state) => {
	return {
		pokemonTypes: state.types,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		setPokemonsOrder: (order) => dispatch(setPokemonsOrder(order)),
		
		setPokemonsType: (type) => dispatch(setPokemonsType(type)),
        GetCreate: () => dispatch(GetCreate()),
        getPokemons: () => dispatch(getPokemons()),
        GetApi: () => dispatch(GetApi()),
        GetTypes: () => dispatch(GetTypes())
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(Filtros);
