import React from 'react'
import '../Filtros/Filtros.css'
import {connect} from 'react-redux'
import {setPokemonsOrder,setPokemonsType,setPokemonsOrigin} from '../../actions/index'
const Filtros = ({pokemonTypes, setPokemonsOrder,setPokemonsOrigin,setPokemonsType}) => {
    const handleFilter = (e) => {
		setPokemonsOrder(e.target.value);
	};

	const handleOrigin = (e) => {
		setPokemonsOrigin(e.target.value);
	};
	const handleTypes = (e) => {
		setPokemonsType(e.target.value);
	};
    
    return (
        <div>
            <select name='Filter' onChange={(e) => handleFilter(e)}>
				<option value='A-Z'>A-Z</option>
				<option value='Z-A'>Z-A</option>
				<option value='More Attack'>More Attack</option>
				<option value='Less Attack'>Less Attack</option>
			</select>
			<select name='Type' onChange={(e) => handleTypes(e)}>
				<option value='All'>All</option>
				{pokemonTypes &&
					pokemonTypes.map((t, i) => (
						<option key={i} value={t.name}>
							{t.name}
						</option>
					))}
			</select>
			<select name='Origin' onChange={(e) => handleOrigin(e)}>
				<option value='All'>All</option>
				<option value='API'>API</option>
				<option value='Companion App'>Companion App</option>
			</select>
        </div>
    )
}

const mapStateToProps = (state) => {
	return {
		pokemonTypes: state.pokemonTypes,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		setPokemonsOrder: (order) => dispatch(setPokemonsOrder(order)),
		setPokemonsOrigin: (origin) => dispatch(setPokemonsOrigin(origin)),
		setPokemonsType: (type) => dispatch(setPokemonsType(type)),
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(Filtros);
