import React, { Fragment , useEffect, useState} from "react";
import { connect } from "react-redux";
import { addPokemons,GetTypes } from "../../actions/index";
import style from "./AddPokemon.module.css";
import axios from 'axios'

export function Creation({pokemonTypes, GetTypes}) {
  const [pokemon, setPokemon] = useState({
    name: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    types:[],
    sprite: ""
  });
  const [Types, setTypes] = useState([]);
  const [pokemonS, setPokemonS] = useState({});
  const [show, setShow] = useState(false);
  
  useEffect(() => {
      GetTypes()        
  },[GetTypes])
  
  useEffect(() => {
      if (Types.length) {
        let total = pokemonTypes.reduce((acc, el) => {
          if (Types.includes(el.name) === true) {
            acc.push(el.id);
          }
          return acc;
        }, []);
        setData({...Data, type: total});
      }
  }, [Types]);
  
  const handleChange = (e) => {
    setPokemon({
      ...pokemon,
      [e.target.name]: e.target.value,
    });
  };
  const handlesubmit = async (e) => {
    e.preventDefault();
    //props.addPokemons(pokemon);
    try {
      await axios.post('http://localhost:3002/pokemons', {pokemon})
        .then(res => {
          console.log(res)
          console.log(res.data)
        })
      
    } catch (err) {
      console.log("error del post"+err);
    }
    setShow(true);
    setPokemonS(pokemon);
    setPokemon({
      name: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
      type:[],
      sprite: ""
    });
  };

  return (
    <div className={style.container}>
      <div className={style.subcontainerform}>
        <form className={style.formcreate} onSubmit={handlesubmit}>
            <label>Name: </label>
            <input className={style.input} name="name" value={pokemon.name} onChange={handleChange} />
            <br/>
            <label>HP:</label>
            <input
            className={style.input}
              name="hp"
              value={pokemon.health}
              onChange={handleChange}
            />
         <br/>
            <label>Attack: </label>
            <input
            className={style.input}
              name="attack"
              value={pokemon.attack}
              onChange={handleChange}
            />
          <br/>
            <label>Defense:</label>
            <input
            className={style.input}
              name="defense"
              value={pokemon.defense}
              onChange={handleChange}
            />
        <br/>
            <label>Speed:</label>
            <input className={style.input} name="speed" value={pokemon.speed} onChange={handleChange} />
            <br/>
            <label>Height:</label>
            <input
            className={style.input}
              name="height"
              value={pokemon.height}
              onChange={handleChange}
            />
      <br/>
            <label>Weight: </label>
            <input
            className={style.input}
              name="weight"
              value={pokemon.weight}
              onChange={handleChange}
            />
       <br/>
            <label>Sprite:</label>
            <input 
              className={style.input}
              name="sprite"
              value={pokemon.sprite}
              onChange={handleChange}
            />
          {
            pokemonTypes && pokemonTypes.map(t=>(
              <p>t.type</p>
            ))
          }
          <button className={style.button} type="submit">enviar</button>
        </form>
      </div>

      <div>
        {show ? (
          <div className={style.subcontainerform2}>
            <div>
              <h4>
                CREADO #{pokemonS.id} {pokemonS.name}
              </h4>
              <img
                src="https://i.pinimg.com/originals/76/ef/60/76ef60010a9ac361291570dd1a4de497.jpg"
                alt="No esta"
              />
            </div>

            <div>
              <ul>
                <li>HP: {pokemonS.health}</li>
                <li>weight: {pokemonS.weight}</li>
                <li>height: {pokemonS.height}</li>
                <li>attack: {pokemonS.attack}</li>
                <li>defense: {pokemonS.defense}</li>
                <li>speed: {pokemonS.speed}</li>
                <div>
                  <img
                    className={style.typeImg}
                    src={`/images/Tipo_Pokemons/${pokemonS.tipo1}.gif`}
                    alt="no encontrada"
                  />
                  <img
                    className={style.typeImg}
                    src={`/images/Tipo_Pokemons/${pokemonS.tipo2}.gif`}
                    alt="no encontrada"
                  />
                </div>
              </ul>
            </div>
          </div>
        ) : (
          ""
        )}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    pokemonTypes: state.types,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addPokemons: (poke) => dispatch(addPokemons(poke)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Creation);
