import '../Home/Home.css';
import React , { useEffect, useState} from 'react'

import {connect} from 'react-redux'
import {getPokemons} from '../../actions/index'
import Navbar from '../Nav/NavBar'
import axios from 'axios'
import {Link} from 'react-router-dom';
import Pokemon from '../Pokemon/Pokemon';
import Pagination from '../Pagination/Pagination'

function Home({getPokemons,state}) {
  const [pokemons, setPokemons] = useState([]);
  const [pokes, setPokes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(12);
  
  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      //const res = await axios.get('http://localhost:3002/pokemons');
      getPokemons()
      setPokes(state);
      setLoading(false);
    };
    fetchPosts();
    console.log("fetchpost"+ state)
  },[])

  useEffect(() => {
      setPokes(state);
      setLoading(false);
  },[])

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPokes = state.slice(indexOfFirstPost, indexOfLastPost);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  return (
    <div>
    <div className="container-pokes">
      <Pokemon posts={currentPokes} loading={loading}/>
    </div>
    <div className="container-pag">
    <Pagination
        postsPerPage={postsPerPage}
        totalPosts={state.length}
        paginate={paginate}
    /> 
    </div>
    </div> 
  );
}
const feliToProps = (state) => {
  return {
    state: state.pokemonHome
  }
}
const mapDispatchToProps = (dispatch) => {
  return{
    getPokemons: () => dispatch(getPokemons())
  }
}
export default connect(feliToProps,mapDispatchToProps)(Home);

