import React, { Fragment , useEffect} from "react";
import { connect } from "react-redux";
import { addPokemons,GetTypes } from "../../actions/index";
import style from "./AddPokemon.module.css";
import axios from 'axios'

export function Creation(props) {
    useEffect(() => {
        GetTypes()
        
    },[])
    useEffect(()=>{
        console.log("tyo"+ props.state)
    })
  const [pokemon, setPokemon] = React.useState({
    
    name: "",
    health: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    types:[]
  });
  const [pokemonS, setPokemonS] = React.useState({});

  const [show, setShow] = React.useState(false);
  const handleChange = (e) => {
    setPokemon({
      ...pokemon,
      [e.target.name]: e.target.value,
    });
  };
  const handlesubmit = async (e) => {
    e.preventDefault();
    //props.addPokemons(pokemon);
    try {
      await axios.post('http://localhost:3002/pokemons', pokemon);
      
    } catch (err) {
      console.log(err);
    }
    setShow(true);
    setPokemonS(pokemon);
    setPokemon({
      
      name: "",
      health: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
      type:[]
    });
  };

  return (
    <div className={style.container}>
      <div className={style.subcontainerform}>
        <form className={style.formcreate} onSubmit={handlesubmit}>
            <label>PokeName: </label>
            <input className={style.input} name="name" value={pokemon.name} onChange={handleChange} />
            <br/>
            <label>HP:</label>
            <input
            className={style.input}
              name="health"
              value={pokemon.health}
              onChange={handleChange}
            />
         <br/>
            <label>Attack: </label>
            <input
            className={style.input}
              name="attack"
              value={pokemon.attack}
              onChange={handleChange}
            />
          <br/>
            <label>Defense:</label>
            <input
            className={style.input}
              name="defense"
              value={pokemon.defense}
              onChange={handleChange}
            />
        <br/>
            <label>Speed:</label>
            <input className={style.input} name="speed" value={pokemon.speed} onChange={handleChange} />
            <br/>
            <label>Height:</label>
            <input
            className={style.input}
              name="height"
              value={pokemon.height}
              onChange={handleChange}
            />
      <br/>
            <label>Weight: </label>
            <input
            className={style.input}
              name="weight"
              value={pokemon.weight}
              onChange={handleChange}
            />
       <br/>

          
          <button className={style.button} type="submit">enviar</button>
        </form>
      </div>

      <div>
        {show ? (
          <div className={style.subcontainerform2}>
            <div>
              <h4>
                CREADO #{pokemonS.id} {pokemonS.name}
              </h4>
              <img
                src="https://i.pinimg.com/originals/76/ef/60/76ef60010a9ac361291570dd1a4de497.jpg"
                alt="No esta"
              />
            </div>

            <div>
              <ul>
                <li>HP: {pokemonS.health}</li>
                <li>weight: {pokemonS.weight}</li>
                <li>height: {pokemonS.height}</li>
                <li>attack: {pokemonS.attack}</li>
                <li>defense: {pokemonS.defense}</li>
                <li>speed: {pokemonS.speed}</li>
                <div>
                  <img
                    className={style.typeImg}
                    src={`/images/Tipo_Pokemons/${pokemonS.tipo1}.gif`}
                    alt="no encontrada"
                  />
                  <img
                    className={style.typeImg}
                    src={`/images/Tipo_Pokemons/${pokemonS.tipo2}.gif`}
                    alt="no encontrada"
                  />
                </div>
              </ul>
            </div>
          </div>
        ) : (
          ""
        )}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    state: state.types,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addPokemons: (poke) => dispatch(addPokemons(poke)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Creation);
