                    label key = { i }
                    className = 'label' > { el.label }:
                    <
                    /label> <
                    /div> <
                    input className = 'input'
                    key = { el.name }
                    name = { el.name }
                    type = 'text'
                    autoComplete = 'off'
                    onChange = {
                        (e) => handleChange(e) }
                    /> <
                    /div>
                ))
        } <
        div className = 'div_types' >
        <
        select onChange = {
            (e) => handleTypes(e) } > {
            pokemonTypes &&
            pokemonTypes.map((t, i) => ( <
                option key = { i }
                value = { i + 1 } > { t } <
                /option>
            ))
        } <
        /select> <
        div className = 'div_type_container' > 🧬Tipos {
            Types &&
                Types.map((el, i) => ( <
                    div key = { i }
                    className = 'div_type' >
                    <
                    label className = 'label' > { el } < /label> <
                    /div>
                ))
        } <
        /div> <
        /div> <
        div className = 'div_submit' >
